select typname, typowner, typlen, typbyval, typtype, typisdefined, typdelim, typelem, typinput, typoutput, typreceive, typsend, typanalyze, typalign, typstorage, typnotnull, typbasetype, typtypmod, typndims, typdefaultbin, typdefault from gp_dist_random('pg_type') where typnamespace <> (select oid from pg_namespace where nspname='gp_toolkit') and typname not like 'pg_toast_%' order by typname;
