Drop database dldb;
Create database dldb;

\c dldb
set time zone PST8PDT;

Drop table if exists delta_t1;

Create table delta_t1(
    a1 integer,
    a2 bigint,
    a3 date,
    a4 time,
    a5 timestamp,
    a6 timestamp with time zone,
    a7 text
    ) with(appendonly=true, orientation=column, compresstype=rle_type);

